// MergeLists: Merges two collections of the same length by alternating between their elements// left: collection of elements to merge// right: collection of elements to mergepublic static int[] MergeLists(int[] left, int[] right){    var merged = new List<int>();    int length = left.Length;
    for (int index = 0; index < length; index++)    {        int first = left[index];        int second = right[index];
        merged.Add(first);        merged.Add(second);    }    return merged.ToArray();}